//get all Author By Id
app.get('/api/authors/:id', async (req, res) => {
  const id = req.params.id;
  // const author = Authors.find(author => author.id == id);
  const author = await Author.findById(id);
  if (author) {
    res.status(200).send({
      data: author,
      message: 'data get succes'
    })
  } else {
    res.status(404).send({
      message: 'data not found'
    })
  }
})
//create Author
app.post('/api/authors', async (req, res) => {
  const {Name,Birthyear,Genre,isDead,isMale,ImageURL} = req.body;
  const newAuthor={
      // id:cripto.randomUUID(),
      Name:Name,
      Birthyear:Birthyear,
      Genre:Genre,
      isDead:isDead === 'diri',
      isMale:isMale === 'male',
      ImageURL:ImageURL
    }
  let author = await Author.create(newAuthor);
  res.status(201).redirect("http://localhost:3000/authors")
})
